package jp.example.model.application.reference.stock;import jp.example.model.domain.models.stock.*;import javax.validation.constraints.*;import java.util.*;public final class SearchStockByStockNoImpl implements SearchStockByStockNo {	@NotNull	private final StockRepository stockRepository;	public SearchStockByStockNoImpl(StockRepository stockRepository) {		this.stockRepository = stockRepository;	}	@Override	public Optional<Stock> execute(StockNo stockNo) {		return stockRepository.find(stockNo);	}}